from playwright.sync_api import expectfrom orangehrm_tests.pages.base_page import BasePageclass LoginPage(BasePage):    URL = "https://opensource-demo.orangehrmlive.com/web/index.php/auth/login"    protected_url = "https://opensource-demo.orangehrmlive.com/web/index.php/dashboard/index"    def login(self, username: str, password: str):        self.goto(self.URL)        self.page.fill("input[placeholder='Username']", username)        self.page.fill("input[placeholder='Password']", password)        self.page.click("button[type='submit']")    def verify_forgot_password_link(self):        self.goto(self.URL)        self.page.click(".oxd-text.oxd-text--p.orangehrm-login-forgot-header")    def verify_linkedin_link(self):        self.goto(self.URL)        self.page.click(" a:nth-child(1) > svg:nth-child(1)")    def verify_facebook_link(self):        self.goto(self.URL)        self.page.click(" a:nth-child(2) > svg:nth-child(1)")    def verify_twitter_link(self):        self.goto(self.URL)        self.page.click(" a:nth-child(3) > svg:nth-child(1)")    def verify_youtube_link(self):        self.goto(self.URL)        self.page.click(" a:nth-child(4) > svg:nth-child(1)")    def verify_orangehrminc_link(self):        self.goto(self.URL)        self.page.click("a[href='http://www.orangehrm.com']")    def verify_failed_access_to_protected_url(self):        self.goto(self.protected_url)    def logout(self):        self.page.get_by_role("listitem").locator("i").click()        self.page.get_by_role("menuitem", name="Logout").click()    def go_to_login_page_url(self):        self.goto(self.URL)